<?phpfunction tns_comment_func($comment, $args) {	?>	<li>		<div class="comment" id="comment-<?php comment_ID(); ?>">			<figure class="comment-media">				<?php echo get_avatar($comment, 64); ?>			</figure>			<div class="comment-body">				<?php if (is_user_logged_in()) : ?>					<a href="#" class="comment-reply"					   data-bs-toggle="modal"					   data-bs-target="#comment-modal"					   data-comment-id="<?php echo esc_attr($comment->comment_ID); ?>"					   data-comment-author="<?php echo esc_attr($comment->comment_author); ?>"					   data-main-comment-content="<?php echo esc_attr($comment->comment_content); ?>"					>پاسخ</a>				<?php endif; ?>				<div class="comment-user">					<h4><?php echo esc_html($comment->comment_author); ?></h4>					<span class="comment-date"><?php echo get_comment_date('j F, Y'); ?></span>				</div>				<div class="comment-content">					<?php if (!$comment->comment_approved) : ?>						<div class="alert alert-info">منتظر تایید ثبت دیدگاه خود باشید!</div>					<?php else : ?>						<p><?php echo esc_html($comment->comment_content); ?></p>					<?php endif; ?>				</div>			</div>		</div>	</li>	<?php}?>