<?phpfunction tns_activation_file() {	global $wpdb;	$table_newsletter = $wpdb->prefix .'tns_newsletter';	$tns_newsletter = "CREATE TABLE IF NOT EXISTS `$table_newsletter` (        `id` int(11) NOT NULL AUTO_INCREMENT,        `email` varchar(256) NOT NULL,        `status` int(1) NOT NULL DEFAULT 1 COMMENT 'active: 1, deactive: 0',        `create_at` datetime NOT NULL DEFAULT current_timestamp(),        `update_at` datetime NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),        PRIMARY KEY (`id`),        UNIQUE KEY `email` (`email`)    ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;";	$table_banner = $wpdb->prefix . 'tns_banner';	$tns_banner = "CREATE TABLE IF NOT EXISTS `$table_banner` (        `id` int(11) NOT NULL AUTO_INCREMENT,        `image_url` varchar(256) NOT NULL,        `title` varchar(256) NOT NULL,        `link_url` varchar(256) NOT NULL,        `create_at` datetime NOT NULL DEFAULT current_timestamp(),        PRIMARY KEY (`id`)    ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;";	$table_sliders = $wpdb->prefix . 'tns_sliders';	$tns_sliders = "CREATE TABLE IF NOT EXISTS `$table_sliders` (        `id` int(11) NOT NULL AUTO_INCREMENT,        `top_title` varchar(256) NOT NULL,        `main_title` varchar(256) NOT NULL,        `sub_title` varchar(256) NOT NULL,        `p_thumbnail` varchar(256) NOT NULL,        `p_image` varchar(256) NOT NULL,        `create_at` datetime NOT NULL DEFAULT current_timestamp(),        `update_at` datetime NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),        PRIMARY KEY (`id`)    ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;";	$table_wishlist = $wpdb->prefix .'tns_wishlist';	$tns_wishlist = "CREATE TABLE IF NOT EXISTS `$table_wishlist` (        `id` int(11) NOT NULL AUTO_INCREMENT,        `p_id` int(11) NOT NULL,        `u_id` int(11) NOT NULL,        `p_title` varchar(256) NOT NULL,        `p_thumbnail` varchar(256) NOT NULL,        `p_permalink` varchar(256) NOT NULL,        `create_at` datetime NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),        PRIMARY KEY (`id`)    ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;";	require_once(ABSPATH . 'wp-admin/includes/upgrade.php');	dbDelta($tns_newsletter);	dbDelta($tns_banner);	dbDelta($tns_sliders);	dbDelta($tns_wishlist);	$options = [		     '_tns_social_media_website'  => [			     'telegram' => '',			     'instagram' => '',			     'discord' => '',			     'linkedin' => '',			     'youtube' =>'',			     'x'=> ''		     ]			,'_tns_settings_set_general'  => [			'wishlist_enable'=>'no',			'newsletter_enable'=>'no',			'exclude_category_id'=>'',			'link_enamad' => '',			'link_zarinpal' => ''		]			,'_tns_settings_set_smtp'     => [			'host'     =>'',			'port'     =>'',			'username' =>'',			'password' =>'',			'from'     =>'',			'FormName' =>''		]			,'_tns_settings_set_contact_us'=>[			'email_contact'       => '',			'phone_number_store'  => '',			'phone_number_mobile' => '',			'start_work_h'        => '',			'end_work_h'          => '',			'start_work'          => '',			'end_work'            =>'',			'working_time_raw'    => '',			'address'             => '',			'iframe_map_url'      => '',		]		];	foreach ($options as $key => $value) {		if (get_option($key) === false) {			update_option($key, $value);		}	}}function tns_deactivation() {}add_action('after_switch_theme', 'tns_activation_file');register_deactivation_hook('switch_theme', 'tns_deactivation');