<?php// Looking to send emails in production? Check out our Email API/SMTP product!function tns_send_mail_config($phpmailer) {	$setting_website_smtp = get_option('_tns_settings_set_smtp');	$phpmailer->isSMTP();	$phpmailer->Host     = $setting_website_smtp['host'];	$phpmailer->SMTPAuth = true;	$phpmailer->Port     =$setting_website_smtp['port'];	$phpmailer->Username = $setting_website_smtp['username'];	$phpmailer->Password = $setting_website_smtp['password'];	// Sender and recipient settings	$phpmailer->From = $setting_website_smtp['from'];;	$phpmailer->FormName = $setting_website_smtp['FormName'];}add_action('phpmailer_init', 'tns_send_mail_config');