<?phpfunction tns_post_thumbnail_default(): string {	$thumb_logo = TNS_URL . '/assets/images/blog/grid/3cols/post-2.jpg';	return "<img  class='img-thumbnail'  src='" . esc_url($thumb_logo) . "' alt='image'>";}function tns_post_thumbnail(): string {	if (has_post_thumbnail()){	  return get_the_post_thumbnail('','full',			[				'class'=>'img-responsive card-img',				'alt'=>get_the_title()		]);	} else{	 return	tns_post_thumbnail_default() ;	}}function tns_post_thumbnail_url(): string {	if (has_post_thumbnail()){		return '<div alt="'.get_the_title().'" class="page-header page-header-big text-center" style="background-image: url('.get_the_post_thumbnail_url().')">';	}else{		return '<div alt="'.get_the_title().'"  class="page-header page-header-big text-center" style="background-image: url('. TNS_URL .'/assets/images/contact-header-bg.jpg'.')">';	}}