<?phpadd_action( 'admin_menu', 'tns_register_menu' );function tns_register_menu() {	add_menu_page(		  'تنظیمات  قالب'		, 'تنظیمات  قالب'		, 'manage_options'		, 'setting_rt_shop'		, 'setting_rt_shop_layout'		, 'dashicons-sos'		, 25	);    add_submenu_page(         'setting_rt_shop'        ,'تنظیمات عمومی'        ,'تنظیمات عمومی'        ,'manage_options'        ,'setting_rt_shop_tab_one'        ,'setting_rt_shop_layout'    );	add_submenu_page(		'setting_rt_shop'		, 'مدریت اسلایدر'		, 'مدیریت اسلایدر'		, 'manage_options'		, 'setting_rt_shop_tab_two'		, 'setting_rt_shop_layout' ///slider_setting_html_layout		, ''	);	add_submenu_page(		'setting_rt_shop'		, 'مدیریت بنر ها'		, 'مدیریت بنر ها'		, 'manage_options'		, 'setting_rt_shop_tab_three'		, 'setting_rt_shop_layout' //banner_setting_html_layout		, ''	);	add_submenu_page(		'setting_rt_shop'		, 'تماس با ما'		, 'تماس با ما'		, 'manage_options'		, 'setting_rt_shop_tab_four'		, 'setting_rt_shop_layout'		, ''	);	add_submenu_page(		'setting_rt_shop'		, 'مدریت شبکه های اجتماعی'		, 'مدریت شبکه های اجتماعی'		, 'manage_options'		, 'setting_rt_shop_tab_five'		, 'setting_rt_shop_layout'		, ''	);}function setting_rt_shop_layout() {	if ( ! current_user_can( 'manage_options' ) ) {		wp_die( 'دسترسی غیرمجاز!' );	}	$page = isset( $_GET['page'] ) ? $_GET['page'] : 'setting_rt_shop_tab_one';	$tab  = str_replace( 'setting_rt_shop_', '', $page );	?>    <div class="wrap">        <h1 class="nav-tab-wrapper">            <a href="?page=setting_rt_shop_tab_one"               class="nav-tab <?php echo $tab === 'tab_one' ? 'nav-tab-active' : ''; ?>">تنظیمات عمومی</a>            <a href="?page=setting_rt_shop_tab_two"               class="nav-tab <?php echo $tab === 'tab_two' ? 'nav-tab-active' : ''; ?>">مدیریت اسلایدر</a>            <a href="?page=setting_rt_shop_tab_three"               class="nav-tab <?php echo $tab === 'tab_three' ? 'nav-tab-active' : ''; ?>">منتخب بنر ها</a>            <a href="?page=setting_rt_shop_tab_four"               class="nav-tab <?php echo $tab === 'tab_four' ? 'nav-tab-active' : ''; ?>">تماس با ما</a>            <a href="?page=setting_rt_shop_tab_five"               class="nav-tab <?php echo $tab === 'tab_five' ? 'nav-tab-active' : ''; ?>">شبکه‌های اجتماعی</a>        </h1>        <div class="tab-content" style="margin-top: 20px;">			<?php			switch ( $tab ) {				case 'tab_one':					general_setting_html_layout();					break;				case 'tab_two':					slider_setting_html_layout();					break;				case 'tab_three':					banner_setting_html_layout();					break;				case 'tab_four':					contact_us_setting_layout();					break;				case 'tab_five':					social_media_html_layout();					break;				default:					info_setup_theme();			}			?>        </div>    </div>	<?php}/// TAB ONEfunction general_setting_html_layout() {	require_once 'view/general-setting-html-layout.php';}/// TAB TWOfunction slider_setting_html_layout() {	require_once 'view/slider-setting-html-layout.php';}//TAB THREEfunction banner_setting_html_layout() {	require_once 'view/banner-setting-html-layout.php';}//TAB FOURfunction contact_us_setting_layout() {	require_once 'view/contact-us-setting-layout.php';}//TAB FIVEfunction social_media_html_layout() {	require_once 'view/social-media-html-layout.php';}//DEFAULTfunction info_setup_theme() {	require_once 'view/info-setup-theme.php';}