<?phpif ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['edit_submit'])) {	if (!isset($_POST['_nonce_tns_edit_banner']) || !wp_verify_nonce($_POST['_nonce_tns_edit_banner'], '_nonce_tns_edit_banner')) {		$message = '<div class="notice notice-error is-dismissible"><p>اعتبارسنجی امنیتی انجام نشد!</p></div>';	} else {		global $wpdb ;		$table = $wpdb->prefix . 'tns_banner';		$id = intval($_POST['edit_id']);		$data = [			'image_url' => esc_url_raw($_POST['edit_image']),			'title' => sanitize_text_field($_POST['edit_title']),			'link_url' => esc_url_raw($_POST['edit_link']),		];		$wpdb->update($table, $data, ['id' => $id], ['%s', '%s', '%s'], ['%d']);		$message = '<div class="notice notice-success is-dismissible"><p>بنر با موفقیت ویرایش شد!</p></div>';	}}