<?phpif (!current_user_can('manage_options')) {	return;}$message = '';include_once 'CRUD/insert-banner.php';include_once 'CRUD/update-banner.php';include_once 'CRUD/delete-banner.php';global $wpdb;$table = $wpdb->prefix  . 'tns_banner'; $home_banners = $wpdb->get_results("SELECT * FROM {$table}", ARRAY_A); ?><div class="uk-container uk-margin-top">	<?php echo $message; ?>    <div class="uk-flex uk-flex-between uk-flex-middle uk-margin-bottom">        <h4 class="uk-text-right uk-margin-remove"><?php echo esc_html(get_admin_page_title()); ?></h4>        <a href="#" uk-toggle="target: #modal-close-default" class="uk-button uk-button-primary">+ افزودن بنر</a>    </div>    <div class="uk-overflow-auto">        <table class="uk-table uk-table-hover uk-table-middle uk-table-divider uk-table-small">            <thead>            <tr>                <th class="uk-text-right">#</th>                <th class="uk-text-right">تصویر</th>                <th class="uk-text-right">عنوان بنر</th>                <th class="uk-text-right">لینک ورود</th>                <th class="uk-text-right">عملیات</th>            </tr>            </thead>            <tbody>			<?php if (!empty($home_banners)) : ?>				<?php $row_number = 1; ?>				<?php foreach ($home_banners as $banner) : ?>                    <tr>                        <td class="uk-text-right"><?php echo $row_number ++ ; ?></td>                        <td class="uk-text-right">                            <div class="uk-flex uk-flex-middle uk-flex-right">                                <img src="<?php echo esc_url($banner['image_url']); ?>" alt="تصویر" class="uk-border-rounded" style="width: 100px; height: auto; object-fit: cover;">                            </div>                        </td>                        <td class="uk-text-right"><?php echo esc_html($banner['title']); ?></td>                        <td class="uk-text-right">                            <a href="<?php echo esc_url($banner['link_url']); ?>" target="_blank"><?php echo esc_url($banner['link_url']); ?></a>                        </td>                        <td class="uk-text-right" style="white-space: nowrap;">                               <a href="<?php echo esc_url(add_query_arg(['action' => 'delete', 'id' => $banner['id']])); ?>" uk-tooltip="title: حذف بنر" class="uk-icon-button" uk-icon="trash"></a>                                   <a href="#edit-banner-modal"                                   uk-toggle                                   uk-tooltip="title: ویرایش بنر"                                   class="uk-icon-button edit-banner-btn"                                   uk-icon="file-edit"                                   data-id="<?php echo esc_attr($banner['id']); ?>"                                   data-image="<?php echo esc_attr($banner['image_url']); ?>"                                   data-title="<?php echo esc_attr($banner['title']); ?>"                                   data-link="<?php echo esc_attr($banner['link_url']); ?>">                                    </a>                        </td>                    </tr>				<?php endforeach; ?>			<?php else : ?>                <tr><td colspan="5" class="uk-text-center">هیچ بنری ثبت نشده است.</td></tr>			<?php endif; ?>            </tbody>        </table>    </div></div><!-- Modal --><div id="modal-close-default" uk-modal>    <div class="uk-modal-dialog uk-modal-body">        <button class="uk-modal-close-default" type="button" uk-close></button>        <h2 class="uk-modal-title">افزودن بنر جدید</h2><div class="uk-alert-primary uk-border-rounded" uk-alert>            <a class="uk-alert-close" uk-close></a>            <div class="uk-flex uk-flex-middle">                <span class="uk-margin-small-right"></span>                <div>                    <h4 class="uk-margin-remove-bottom">راهنمای عنوان بنر</h4>                    <p class="uk-margin-small-top">     