<?phpif ( ! current_user_can( 'manage_options' ) ) {	return;}$message         = '';$setting_website = get_option( '_tns_settings_set_contact_us', [] );if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['submit'])) {	if (!isset($_POST['_nonce_tns_setting_general']) || !wp_verify_nonce($_POST['_nonce_tns_setting_general'], '_nonce_tns_setting_general')) {		$message = '<div class="notice notice-error is-dismissible"><p>اعتبارسنجی امنیتی انجام نشد!</p></div>';	} else {		$time_work_parts = explode('|', sanitize_text_field($_POST['working-time'] ?? ''));		$setting_data = [			'email_contact'       => sanitize_text_field($_POST['email-contact-us'] ?? ''),			'phone_number_store'  => sanitize_text_field($_POST['phone-number-store'] ?? ''),			'phone_number_mobile' => sanitize_text_field($_POST['phone-number-1'] ?? ''),			'start_work_h'        => $time_work_parts[0] ?? '',			'end_work_h'          => $time_work_parts[1] ?? '',			'start_work'          => $time_work_parts[2] ?? '',			'end_work'            => $time_work_parts[3] ?? '',			'working_time_raw'    => sanitize_text_field($_POST['working-time'] ?? ''),			'address'             => sanitize_text_field($_POST['address-c-us'] ?? ''),			'iframe_map_url'      => sanitize_text_field($_POST['address-fs'] ?? ''),		];		update_option('_tns_settings_set_contact_us', $setting_data);		$setting_website = $setting_data;		$message = '<div class="notice notice-success is-dismissible"><p>تنظیمات با موفقیت ذخیره شد!</p></div>';	}}?><div class="uk-container">	<?php echo $message; ?>    <div class="uk-flex-inline uk-flex-stretch uk-margin-top">        <h4 class="uk-margin-left uk-text-right">            تنظیمات            <span class="uk-text-primary"><?php echo esc_html( get_admin_page_title() ); ?></span>            <span uk-icon="question"                  uk-tooltip="title: با تکمیل فیلدهای زیر، اطلاعات در صفحه تماس با ما نمایش داده می‌شود; pos: left"                  class="uk-text-primary uk-margin-left"                  style="cursor: help;"></span>        </h4>    </div>    <form method="post" class="uk-grid-small" uk-grid>        <div class="uk-flex-inline uk-flex-stretch uk-margin-top">            <div class="uk-grid-small" uk-grid>                <div class="uk-width-1-4@s">                    <span class="uk-badge uk-margin-bottom">ایمیل</span>                    <input class="uk-input" type="text" name="email-contact-us"                           placeholder="ایمیل"                           value="<?php echo esc_attr($setting_website['email_contact'] ?? ''); ?>"                           aria-label="ایمیل"                           uk-tooltip="title: ایمیل مثل contact@example.com ; pos:bottom-right">                </div>                <div class="uk-width-1-4@s">                    <span class="uk-badge uk-margin-bottom">شماره تماس فروشگاه</span>                    <input class="uk-input" type="text" name="phone-number-store"                           placeholder="شماره تماس فروشگاه"                           value="<?php echo esc_attr($setting_website['phone_number_store'] ?? ''); ?>"                           aria-label="شماره تماس"                           uk-tooltip="title: شماره تماس ثابت فروشگاه ; pos:bottom-right">                </div>                <div class="uk-width-1-4@s">                    <span class="uk-badge uk-margin-bottom">شماره موبایل</span>                    <input class="uk-input" type="text" name="phone-number-1"                           placeholder="شماره موبایل"                           value="<?php echo esc_attr($setting_website['phone_number_mobile'] ?? ''); ?>"                           aria-label="شماره موبایل"                           uk-tooltip="title: شماره موبایل تماس مثال: 09123456789 ; pos:bottom-right">                </div>                <div class="uk-width-1-4@s">                    <span class="uk-badge uk-margin-bottom">ساعت کاری</span>                    <input class="uk-input" type="text" name="working-time"                           placeholder="مثال:  ساعت ورود روز پتجشنبه  :  8 | ساعت تعطیل شدن فروشگاه : 15 |ساعت ورود: 7 | ساعت خروج : 18"                           value="<?php echo esc_attr($setting_website['working_time_raw'] ?? ''); ?>"                           aria-label="ساعت کاری"                           uk-tooltip="title: فرمت ورودی مثل: 8|15|7|18 برای شروع و پایان صبح و عصر ; pos:bottom-right">                </div>                <div class="uk-width-1-1@l">                    <span class="uk-badge uk-margin-bottom">آدرس فیزیکی</span>                    <input class="uk-input" type="text" name="address-c-us"                           placeholder="آدرس"                           value="<?php echo esc_attr($setting_website['address'] ?? ''); ?>"                           aria-label="آدرس"                           uk-tooltip="title: آدرس محل کار خود را وارد کنید. ; pos:bottom-right">                </div>                <div class="uk-width-1-1@l">                    <span class="uk-badge uk-margin-bottom">آدرس نقشه گوگل (iframe src)</span>                    <input class="uk-input" type="text" name="address-fs"                           placeholder="آدرس URL نقشه گوگل"                           value="<?php echo esc_attr($setting_website['iframe_map_url'] ?? ''); ?>"                           aria-label="نقشه"                           uk-tooltip="title: src آدرس iframe نقشه گوگل را وارد کنید ; pos:bottom-right">                </div>                <div class="uk-width-1-1">					<?php submit_button('ذخیره تنظیمات', 'primary'); ?>					<?php wp_nonce_field('_nonce_tns_setting_general', '_nonce_tns_setting_general'); ?>                </div>            </div>            <hr class="uk-divider-icon uk-margin">        </div>    </form></div>