<?phpif ( ! current_user_can( 'manage_options' ) ) {	return;}$message = '';$setting_website_general = get_option( '_tns_settings_set_general', [] );$setting_website_smtp = get_option('_tns_settings_set_smtp',[]);if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['submit'])) {if (!isset($_POST['_nonce_tns_setting_general']) || !wp_verify_nonce($_POST['_nonce_tns_setting_general'], '_nonce_tns_setting_general')) {	$message = '<div class="notice notice-error is-dismissible"><p>اعتبارسنجی امنیتی انجام نشد!</p></div>';} else {	$setting_website_general_data  = [         'wishlist_enable'=>sanitize_text_field($_POST['wishlist_enable'] ?? 'no'),         'newsletter_enable'=>sanitize_text_field($_POST['newsletter_enable'] ?? 'no'),         'exclude_category_id'=>sanitize_text_field($_POST['exclude_category'] ?? ' '),         'link_enamad' => sanitize_text_field($_POST['input-enamad'] ?? ' '),         'link_zarinpal' => sanitize_text_field($_POST['input-zarinpal'] ?? ' ')    ];	$setting_website_smtp_data = [		'host'     =>sanitize_text_field($_POST['host-fs'] ?? ' '),		'port'     =>sanitize_text_field($_POST['port-fs'] ?? ' '),	    'username' =>sanitize_text_field($_POST['username-fs'] ?? ' '),        'password' =>sanitize_text_field($_POST['password-fs'] ?? ' '),        'from'     =>sanitize_text_field($_POST['from-fs'] ?? ' '),        'FormName' =>sanitize_text_field($_POST['FormName-fs'] ?? ' ')    ];	update_option('_tns_settings_set_general', $setting_website_general_data);    update_option('_tns_settings_set_smtp',$setting_website_smtp_data);	$setting_website_general = $setting_website_general_data;    $setting_website_smtp = $setting_website_smtp_data ;	$message = '<div class="notice notice-success is-dismissible"><p>تنظیمات با موفقیت ذخیره شد!</p></div>';}}?><div class="uk-container">	<?php echo $message; ?>    <div class="uk-flex-inline uk-flex-stretch uk-margin-top">        <h4 class="uk-margin-left uk-text-right">            <span class="uk-text-primary"><?php echo esc_html( get_admin_page_title() ); ?></span>        </h4>    </div>    <form method="post" class="uk-grid-small" uk-grid>        <div class="uk-width-1-1">            <div class="uk-margin">                <label>                    <input class="uk-checkbox" type="checkbox" name="wishlist_enable" value="yes" <?php checked( $setting_website_general['wishlist_enable'] ?? 'no', 'yes' ); ?>>                    فعال کردن سیستم لیست علاقه مندی محصول                </label>                <span                        uk-icon="info"                        uk-tooltip="title: در صورت فعال‌سازی، لیست علاقه‌مندی محصولات نمایش داده می‌شود. برای استفاده از این قابلیت، یک برگه با نامک «wishlist» ساخته و شرت‌کد [tns-short-code-wishlist] را داخل آن قرار دهید.; pos: left; delay: 300; animation: true"                        style="cursor: help;"                ></span>            </div>            <div class="uk-margin">                <label>                    <input class="uk-checkbox" type="checkbox" name="newsletter_enable" value="yes" <?php checked( $setting_website_general['newsletter_enable'] ?? 'no', 'yes' ); ?>>                    فعال کردن سیستم خبر نامه وب سایت                </label>                <span                        uk-icon="info"                        uk-tooltip="title:درصورت زد تیک تایید سیستم خبرنامه فعال میشود!; pos: left; delay: 300; animation: true"                        style="cursor: help;"                ></span>            </div>        </div>        <div class="uk-width-1-1">            <div class="uk-grid uk-child-width-1-4@s uk-grid-small uk-margin">                <div>                    <label>                        <div class="uk-margin-bottom">                            <span class="uk-badge">غیر فعال کردن دسته بندی</span>                            <span                                    uk-icon="info"                                    uk-tooltip="title:آی دی دسته بندی نشده را در اینجا قرار دهید.; pos: left; delay: 300; animation: true"                                    style="cursor: help;"                            ></span>                        </div>                        <input class="uk-input" type="text" name="exclude_category"                               value="<?php echo esc_attr($setting_website_general['exclude_category_id']) ?? ' ' ?>"                               placeholder="آی دی را وارد کنید"                               aria-label="غیر فعال کردن دسته بندی نشده محصولات">                    </label>                </div>            </div>        </div>        <!-- سرفصل SMTP -->        <div class="uk-width-1-1">            <hr class="uk-divider-icon">            <h4 class=" uk-text-right">تنظیمات  <span class="uk-text-danger">SMTP</span> وب‌ سایت</h4>        </div>        <!-- SMTP Inputs -->        <div class="uk-width-1-2@s">            <span class="uk-badge uk-margin-bottom">Host</span>            <input class="uk-input" value="<?php echo $setting_website_smtp['host'] ?? ' '?>" type="text" name="host-fs" placeholder="مثال: smtp.example.com" uk-tooltip="title:آدرس سرور SMTP خود را وارد کنید. مثال: smtp.example.com; pos:bottom-right">        </div>        <div class="uk-width-1-2@s">            <span class="uk-badge uk-margin-bottom">Port</span>            <input class="uk-input" value="<?php echo $setting_website_smtp['port'] ?? ' '?>" type="text" name="port-fs" placeholder="مثال: 587"  uk-tooltip="title:پورت مورد استفاده برای اتصال SMTP. معمولاً 587 برای TLS یا 465 برای SSL; pos: bottom-right">        </div>        <div class="uk-width-1-2@s">            <span class="uk-badge uk-margin-bottom">Username</span>            <input class="uk-input" value="<?php echo $setting_website_smtp['username'] ?? ' '?>" type="text" name="username-fs" placeholder="نام کاربری SMTP"  uk-tooltip="title:نام کاربری حساب SMTP شما. معمولاً آدرس ایمیل کامل شماست; pos: bottom-right">        </div>        <div class="uk-width-1-2@s">            <span class="uk-badge uk-margin-bottom">Password</span>            <input class="uk-input" value="<?php echo $setting_website_smtp['password'] ?? ' '?>" type="password" name="password-fs" placeholder="رمز عبور SMTP"  uk-tooltip="title:رمز عبور اختصاص داده شده برای حساب SMTP شما; pos: bottom-right">        </div>        <div class="uk-width-1-2@s">            <span class="uk-badge uk-margin-bottom">From Email</span>            <input class="uk-input" value="<?php echo $setting_website_smtp['from'] ?? ' '?>" type="email" name="from-fs" placeholder="مثال: no-reply@example.com"  uk-tooltip="title:آدرس ایمیلی که می‌خواهید به عنوان فرستنده نمایش داده شود; pos: bottom-right">        </div>        <div class="uk-width-1-2@s">            <span class="uk-badge uk-margin-bottom">From Name</span>            <input class="uk-input" value="<?php echo $setting_website_smtp['FormName']  ?? ' '?>" type="text" name="FormName-fs" placeholder="مثال: نام وبسایت شما" uk-tooltip="title:نامی که می‌خواهید به عنوان فرستنده نمایش داده شود;pos: bottom-right">        </div>        <!-- Divider -->        <div class="uk-width-1-1">            <hr class="uk-divider-icon">        </div>            <div class="uk-width-1-2@s">                <span class="uk-badge uk-margin-bottom">لینک ای نماد</span>                <input class="uk-input" value="<?php echo $setting_website_general['link_enamad'] ?? ' ' ?>" name="input-enamad" type="text" placeholder="لینک خود را وارد کنید..." aria-label="input-enamad">            </div >            <div class="uk-width-1-2@s" >                <span class="uk-badge uk-margin-bottom">زرین پال</span>                <input class="uk-input" name="input-zarinpal" type="text" value="<?php echo $setting_website_general['link_zarinpal' ] ?? ' ' ?>" placeholder="لینک زرین پال..." aria-label="زرین پال">            </div>        <div class="uk-width-1-1">			<?php submit_button('ذخیره تنظیمات', 'primary'); ?>			<?php wp_nonce_field('_nonce_tns_setting_general', '_nonce_tns_setting_general'); ?>        </div>    </form></div>