<?phpif (!current_user_can('manage_options')) {	return;}$message = '';$social_media_options = get_option('_tns_social_media_website', []);// Process form submissionif ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['submit'])) {	// Verify nonce	if (!isset($_POST['_nonce_social_media']) || !wp_verify_nonce($_POST['_nonce_social_media'], '_nonce_social_media')) {		$message = '<div class="notice notice-error is-dismissible"><p>اعتبارسنجی امنیتی انجام نشد!</p></div>';	} else {		// Sanitize all inputs		$social_data = [			'telegram' => sanitize_text_field($_POST['telegram-f'] ?? ''),			'instagram' => sanitize_text_field($_POST['instagram-f'] ?? ''),			'discord' => sanitize_text_field($_POST['discord-f'] ?? ''),			'linkedin' => sanitize_text_field($_POST['linkedin-f'] ?? ''),			'youtube' => sanitize_text_field($_POST['youtube-f'] ?? ''),			'x'=> sanitize_text_field($_POST['x-f'] ?? ''),		];		// Save options		update_option('_tns_social_media_website', $social_data);		$message = '<div class="notice notice-success is-dismissible"><p>تنظیمات با موفقیت ذخیره شد!</p></div>';	}}?><div class="uk-container">	<?php if (!empty($message)) { echo $message; } ?>	<div class="uk-flex-inline uk-flex-stretch uk-margin-top">		<h4 class="uk-margin-left uk-text-right"><?php echo esc_html(get_admin_page_title()); ?></h4>	</div>	<form method="post" class="social_media">		<div class="uk-child-width-1-1@s uk-grid-small" uk-grid>			<?php			$fields = [				'telegram'  => ['Telegram', 'telegram', 'ایدی تلگرام خود را وارد کنید'],				'instagram' => ['Instagram', 'instagram', 'ایدی اینستاگرام خود را وارد کنید'],				'discord'   => ['Discord', 'discord', 'ایدی دیسکورد خود را وارد کنید'],				'linkedin'  => ['LinkedIn', 'linkedin', 'ایدی لینکدین خود را وارد کنید'],				'youtube'   => ['YouTube', 'youtube', 'ایدی یوتیوب خود را وارد کنید'],				'x'         => ['X', 'x', 'ایدی ایکس خود را وارد کنید'],			];			foreach ($fields as $key => [$label, $icon, $tooltip]): ?>				<div class="uk-margin uk-flex uk-flex-middle">					<span class="uk-badge uk-margin-small-left"><?php echo esc_html($label); ?></span>					<div class="uk-inline uk-width-1-2@s">						<span class="uk-form-icon" uk-icon="icon: <?php echo esc_attr($icon); ?>"></span>						<input class="uk-input" name="<?php echo esc_attr($key); ?>-f" type="text"						       value="<?php echo esc_attr($social_media_options[$key] ?? ''); ?>"						       uk-tooltip="title:<?php echo esc_attr($tooltip); ?>; pos: left"						       placeholder="مثال: https://www.<?php echo esc_attr($icon); ?>.com/@myid">					</div>				</div>			<?php endforeach; ?>		</div>		<div class="uk-margin-top">			<?php submit_button('ذخیره تنظیمات'); ?>			<?php wp_nonce_field('_nonce_social_media', '_nonce_social_media'); ?>		</div>	</form></div>