<?phpadd_shortcode('tns-short-code-wishlist','tns_wishlist_func');function tns_wishlist_func(): string {    if (!is_user_logged_in()){        ?>        <div class="alert alert-info">ابتدا وارد شوید!!</div>	    <?php    }	global $wpdb;	$table = $wpdb->prefix . 'tns_wishlist' ;	$user_id = get_current_user_id();	$stmt = $wpdb->get_results($wpdb->prepare("SELECT * FROM {$table} WHERE u_id='%d' ",$user_id));	ob_start(); ?>    <main class="main">        <div class="page-header text-center" style="background-image: url('. TNS_URL . '/assets/images/page-header-bg.jpg'.')">            <div class="container">                <h1 class="page-title"><?php echo get_the_title(); ?></span></h1>            </div><!-- End .container -->        </div><!-- End .page-header -->        <nav aria-label="breadcrumb" class="breadcrumb-nav">            <div class="container">                <ol class="breadcrumb">					<?php echo Breadcrumb::tns_get_breadcrumb(); ?>                </ol>            </div><!-- End .container -->        </nav><!-- End .breadcrumb-nav -->        <div class="page-content">            <div class="container">				<?php if ($stmt) : ?>                    <table class="table table-wishlist table-mobile">                        <thead>                        <tr>                            <th>محصول</th>                            <th>قیمت</th>                            <th>وضعیت محصول</th>                            <th></th>                            <th></th>                        </tr>                        </thead>                        <tbody>						<?php foreach ($stmt as $item): ?>							<?php   $product = wc_get_product($item->p_id); ?>                            <tr>                                <td class="product-col">                                    <div class="product">                                        <figure class="product-media">                                            <a href="<?php echo $item->p_permalink ;  ?>">                                                <img src="<?php echo  $item->p_thumbnail ;  ?>" alt="تصویر محصول">                                            </a>                                        </figure>                                        <h3 class="product-title">                                            <a href="<?php echo $item->p_permalink ;  ?>"><?php echo $item->p_title ?></a>                                        </h3><!-- End .product-title -->                                    </div><!-- End .product -->                                </td>                                <td class="price-col"><?php echo $product->get_price();  ?></td>                                <td class="stock-col"><span class="in-stock"><?php echo  $product->is_in_stock() ? 'موجود' : 'ناموجود' ?></span></td>                                <td class="action-col">                                    <a  href="<?php echo esc_url($product->add_to_cart_url()); ?>" class="btn btn-block btn-outline-primary-2"><i                                                class="icon-cart-plus"></i>افزودن به سبد خرید</a>                                </td>                                <td class="remove-col text-left"><!--                                    <button class="btn-remove" data-id="--><?php //echo $item->id; ?><!--"><i class="icon-close"></i></button>-->                            </tr>						<?php endforeach; ?>                        </tbody>                    </table><!-- End .table table-wishlist -->				<?php else : ?>                    <div class="alert alert-info">هیچ محصولی شما انتخاب نکردید</div>				<?php endif; ?>            </div><!-- End .container -->        </div><!-- End .page-content -->    </main><!-- End .main -->	<?php	return ob_get_clean();}