<?phpclass CatWidget extends WP_Widget {	function __construct() {		parent::__construct(			'CatWidget', // Base ID			'tns_cat_widget',			array( 'description' => esc_html__( 'A Foo Widget', 'text_domain' ), ) // Args		);	}	public function widget( $args, $instance ) {		echo $args['before_widget'];		if ( ! empty( $instance['title'] ) ) {			echo $args['before_title'] . apply_filters( 'widget_title', $instance['title'] ) . $args['after_title'];		}		echo '<ul>';		$args_cat = [			'taxonomy' => 'category',			'hide_empty' => false,			'exclude' => 1,			'parent' => 0,			'orderby' => 'id',			'order' => 'ASC'		];		$categories = get_categories($args_cat);		if ( $categories ) {			foreach ( $categories as $category ) {				$count    = $category->count;				$cat_link = get_category_link( $category->term_id );				$cat_name = esc_html( $category->name );                echo '<li>';				echo '<a href="'.$cat_link.'">'.$cat_name.'<span>'.$count.'</span></a>';				echo '</li>';			}		}		echo '</ul>';		echo $args['after_widget'];	}	/**	 * Back-end widget form.	 *	 * @param array $instance Previously saved values from database.	 *	 * @see WP_Widget::form()	 *	 */	public function form( $instance ) {		$title = ! empty( $instance['title'] ) ? $instance['title'] : esc_html__( 'New title', 'text_domain' );		?>        <p>            <label for="<?php echo esc_attr( $this->get_field_id( 'title' ) ); ?>"><?php esc_attr_e( 'Title:', 'text_domain' ); ?></label>            <input class="widefat" id="<?php echo esc_attr( $this->get_field_id( 'title' ) ); ?>"                   name="<?php echo esc_attr( $this->get_field_name( 'title' ) ); ?>" type="text"                   value="<?php echo esc_attr( $title ); ?>">        </p>		<?php	}	public function update( $new_instance, $old_instance ) {		$instance          = array();		$instance['title'] = ( ! empty( $new_instance['title'] ) ) ? sanitize_text_field( $new_instance['title'] ) : '';		return $instance;	}}function tns_register_widget() {	register_widget( 'CatWidget' );}add_action( 'widgets_init', 'tns_register_widget' );