<?phpfunction tns_register_sidebar() {	$args1 = [		'name'          => 'tns_theme_cat_post_sidebar',		'id'            => 'sidebar-1',		'description'   => 'ساید بار قالب',		'before_widget' => '<div id="%1$s" class="widget %2$s">',		'after_widget'  => '</div>',		'before_title'  => '<h6 class="widget-title">',		'after_title'   => '</h6>',		'class' =>'widget widget-cats'	];	register_sidebar($args1);}add_action('widgets_init','tns_register_sidebar');