<?phpadd_action('after_setup_theme','tns_theme_setup');function tns_theme_setup() {	require_once TNS_DIR.'/class/nav_walker/bootstrap_5_wp_nav_menu_walker.php';	register_nav_menu('main-menu', 'Main menu');	register_nav_menu('footer-menu-useful-link', 'Footer Menu Useful Link');	register_nav_menu('footer-menu-category', 'Footer Menu Category');	register_nav_menu('footer-menu-customer-service', 'Footer Menu Customer Service');	//ADD THUMB YO THEME	add_theme_support('woocommerce');	add_theme_support('post-thumbnails');	//ADD UPLOAD IMAGE	add_image_size('my_size', '300','300',array('center','center') ,);	//REMOVE NAV BAE	add_filter('show_admin_bar','__return_false');}