<?phpclass Breadcrumb {	public static function tns_get_breadcrumb(): string {		$html = '<li class="breadcrumb-item"><a href="'.home_url().'">خانه</a></li>';		if (is_single()){			$category = get_the_category();			if (!empty($category)) {				$cat_link = get_category_link($category[0]->term_id);				$html .= '<li class="breadcrumb-item"><a href="' . esc_url($cat_link) . '">' . esc_html($category[0]->name) . '</a></li>';			}			$html .= '<li class="breadcrumb-item active" aria-current="page">'.get_the_title().'</li>';		}		if (is_page(['contact-us','about-us','wishlist','blog','privacy-policy'])){			$html .= '<li class="breadcrumb-item active" aria-current="page">'.get_the_title().'</li>';		}		if (is_404()){			$html .='<li class="breadcrumb-item active" aria-current="page">404</li>';		}		if (is_search()){			$html .='<li class="breadcrumb-item active" aria-current="page">'.get_search_query().'</li>';		}		if (is_tag()){			$html .='<li class="breadcrumb-item active" aria-current="page"> # ' . single_tag_title( '', false ) .'</li>';		}		if (is_category()){			$html .='<li class="breadcrumb-item active" aria-current="page">  ' . single_cat_title('',false) .'</li>';		}		if (!is_page(['contact-us','about-us','wishlist','blog','privacy-policy']) && !is_single() && !is_404() && !is_category() && !is_tag() && !is_author() && !is_search()){			$html .='<li class="breadcrumb-item active" aria-current="page">'.get_the_title().'</li>';		}		return $html;	}}