<div class="comments">	<?php	$comment_num = get_comments_number();	if ($comment_num > 0): ?>        <h3 class="title"><?php echo esc_html($comment_num); ?> دیدگاه</h3>	<?php else: ?>        <h3 class="title">شما اولین کسی باشید که کامنت می‌گذارید!</h3>	<?php endif; ?>    <ul class="comment-list">		<?php		wp_list_comments([			'avatar_size' => 45,			'style'       => 'ul',			'callback'    => 'tns_comment_func',			'max_depth'   => 2,		]);		?>    </ul></div><!-- End .comments --><div class="reply">	<div class="heading">		<h3 class="title">ارسال یک دیدگاه</h3><!-- End .title -->		<p class="title-desc">ایمیل شما منتشر نخواهد شد، فیلد های اجباری با علامت * مشخص			شده اند.</p>	</div><!-- End .heading -->	<?php if (comments_open()) : ?>    <!-- Comment Modal -->        <div class="modal fade" id="comment-modal" tabindex="-1" aria-labelledby="commentModalLabel" aria-hidden="true">            <div class="modal-dialog modal-dialog-centered">                <div class="modal-content">                    <div class="modal-header">                        <h5 class="modal-title reply-to"></h5>                        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>                    </div>                    <div class="modal-body">                        <div class="alert alert-success main-comment-content"></div>                        <form action="<?php echo site_url() . '/wp-comments-post.php'; ?>" method="post">                            <div class="row">								<?php if ( ! is_user_logged_in() ): ?>                                    <div class="col-lg-6">                                        <div class="form-group">                                            <input type="text" class="form-control" name="author" placeholder="نام کامل" required>                                        </div>                                    </div>                                    <div class="col-lg-6">                                        <div class="form-group">                                            <input type="email" class="form-control" name="email" placeholder="ایمیل معتبر" required>                                        </div>                                    </div>                                    <div class="col-lg-12">                                        <p class="comment-form-cookies-consent">                                            <input id="wp-comment-cookies-consent" name="wp-comment-cookies-consent" type="checkbox" value="yes">                                            <label for="wp-comment-cookies-consent">ذخیره نام، ایمیل و وبسایت من در مرورگر برای زمانی که دوباره دیدگاهی می‌نویسم.</label>                                        </p>                                    </div>								<?php endif; ?>                                <div class="col-lg-12">                                    <div class="form-group">                                        <textarea name="comment" class="form-control" cols="30" rows="6" placeholder="نظر خود را بنویسید..." required></textarea>                                    </div>                                </div>                            </div>                            <div class="modal-footer">                                <button type="submit" class="btn btn-primary">ثبت</button>                                <input type="hidden" name="comment_post_ID" value="<?php echo get_the_ID(); ?>" id="comment_post_ID">                                <input type="hidden" name="comment_parent" id="comment_parent" value="0">								<?php if (is_user_logged_in()): ?>                                    <input type="hidden" id="_wp_unfiltered_html_comment_disabled" name="_wp_unfiltered_html_comment" value="<?php echo wp_create_nonce(''); ?>">								<?php endif; ?>                            </div>                        </form>                    </div>                </div>            </div>        </div>    <div class="heading">		<?php if (is_user_logged_in()): ?>		<?php $user = wp_get_current_user(); ?>            <p class="title-desc alert alert-info"><span class="text-danger"><?php echo $user->display_name; ?></span> عزیز، می‌توانید دیدگاه خود را بنویسید!</p>	<?php endif; ?>    </div>    <!-- Comment Form Outside Modal -->	<?php if (get_option('comment_registration') && !is_user_logged_in()) : ?>        <div class="title-desc alert alert-info">کاربر گرامی قبل از ثبت دیدگاه باید ثبت نام کنید!</div>	<?php else: ?>            <form action="<?php echo site_url('/wp-comments-post.php'); ?>" method="post">                <label for="comment" class="sr-only">دیدگاه</label>                <textarea name="comment" id="comment" cols="30" rows="4"                          class="form-control" required placeholder="دیدگاه شما *"></textarea>				<?php if (!is_user_logged_in()): ?>                    <div class="row">                        <div class="col-md-6">                            <label for="author" class="sr-only">نام</label>                            <input type="text" class="form-control" id="author" name="author"                                   required placeholder="نام شما *">                        </div>                        <div class="col-md-6">                            <label for="email" class="sr-only">ایمیل</label>                            <input type="email" class="form-control" id="email" name="email"                                   required placeholder="ایمیل شما *">                        </div>                    </div>				<?php endif; ?>                <button type="submit" class="btn btn-outline-primary-2 float-right">                    <span>ارسال دیدگاه</span>                    <i class="icon-long-arrow-left"></i>                </button>                <input type="hidden" name="comment_post_ID" value="<?php echo get_the_ID(); ?>" id="comment_post_ID">                <input type="hidden" name="comment_parent" id="comment_parent" value="0">            </form>		<?php endif; ?>    <?php endif ?></div><!-- End .reply -->