<?phpclass GoogleReferer {	public static function tns_set_google_referer( int $postID, $url ) {         if (stripos($url ,'https://www.google.com')){		$google_referer_key = '_tns_referer_key_num';		$google_referer_num = get_post_meta( $postID, $google_referer_key, true );		if ( ! metadata_exists( $postID, $google_referer_key, true ) ) {			add_post_meta( $postID, $google_referer_key, '1' );		} else {			$google_referer_num ++;			update_post_meta( $postID, $google_referer_key, $google_referer_num );		}	}}	public static function tnw_get_google_referer(int $postID) {		$google_referer_key = '_tns_referer_key_num';		$google_referer_num = get_post_meta( $postID, $google_referer_key, true );		if ( ! metadata_exists( $postID, $google_referer_key, true ) ) {         return '0';		}else{			return $google_referer_num;		}	}}