<?phpclass PostView {	public static function tns_set_post_view(int $PostID):void {		$view_meta_key = '_tns_post_view';		$post_view = get_post_meta($PostID,$view_meta_key,true);		if (!metadata_exists('post',$PostID,$view_meta_key)){			add_post_meta($PostID,$view_meta_key,'1');		}else{			$post_view++;			update_post_meta($PostID,$view_meta_key,$post_view);		}    }	public static function tns_get_post_view(int $PostID):string {		$view_num_meta_key = '_tns_post_view';		$get_post_view_num = get_post_meta($PostID,$view_num_meta_key,true);		if (!metadata_exists('post',$PostID,$view_num_meta_key)){			return "0" ;		}else{			 return $get_post_view_num;		}	}}