<?phpclass ReadPost {	public static function tns_read_post_time(string $content, int $wpm = 250): int {		$clean_content = strip_tags(strip_shortcodes($content));		$clean_content = preg_replace('/\s+/u', ' ', $clean_content);		$char_count = mb_strlen($clean_content, 'UTF-8');		$persian_chars_per_word = 3.5;		$words = $char_count / $persian_chars_per_word;		$reading_time = ceil($words / $wpm);		return max(1, $reading_time);	}}