<?php$paged = max( 1, get_query_var( 'paged' ) );$args  = [	'post_type'      => 'post',	'posts_per_page' => 6,	'paged'          => $paged,];$query = new WP_Query( $args );if ( $query->have_posts() ):	while ( $query->have_posts() ): $query->the_post(); ?>        <article class="entry entry-list">            <div class="row align-items-center">                <div class="col-md-5">                    <figure class="entry-media">                        <a href="<?php echo get_the_permalink(); ?>">							<?php echo tns_post_thumbnail(); ?>                        </a>                    </figure><!-- End .entry-media -->                </div><!-- End .col-md-5 -->                <div class="col-md-7">                    <div class="entry-body">                        <div class="entry-meta">                                                <span class="entry-author">                                              <?php the_author() ?>                                                </span>                            <span class="meta-separator">|</span>                            <a><?php echo get_the_date( 'j F Y' ); ?></a>                            <span class="meta-separator">|</span>                            <a><?php comments_number(); ?></a>                        </div><!-- End .entry-meta -->                        <h2 class="entry-title">                            <a href="<?php echo get_the_permalink(); ?>"><?php echo get_the_title(); ?></a>                        </h2><!-- End .entry-title -->                        <div class="entry-cats">							<?php							$terms = get_the_terms( get_the_ID(), 'category' );							if ( ! empty( $terms ) && ! is_wp_error( $terms ) ) {								$term_links = array_map( function ( $term ) {									return '<a href="' . esc_url( get_term_link( $term ) ) . '">' . esc_html( $term->name ) . '</a>';								}, $terms );								echo implode( ', ', $term_links );							}							?>                        </div><!-- End .entry-cats -->                        <div class="entry-content">                            <p><?php echo PostExcerpt::tns_post_excerpt(); ?></p>                            <a href="<?php the_permalink(); ?>" class="read-more">ادامه                                خواندن</a>                        </div><!-- End .entry-content -->                    </div><!-- End .entry-body -->                </div><!-- End .col-md-7 -->            </div><!-- End .row -->        </article>	<?php endwhile;else: ?>    <div class="alert alert-info">هیچ پستی منتشر نشده است!</div><?php endif; ?><!-- End .entry-container --><?php echo Pagination::paginate( $query ); ?>