<?php$args = [	'post_type'      => 'post',	'posts_per_page' => 6,	'order'          => 'ASC'];$the_query = new WP_Query($args);if ($the_query->have_posts()) :	while ($the_query->have_posts()) : $the_query->the_post();  ?>		<article class="entry">			<figure class="entry-media">				<a href="<?php echo get_the_permalink(); ?>">					<?php echo tns_post_thumbnail(); ?>				</a>			</figure><!-- End .entry-media -->			<div class="entry-body text-center">				<div class="entry-meta">					<a href="#"><?php echo get_the_date('j F Y'); ?></a>					<span class="meta-separator">|</span>					<?php comments_number(); ?>				</div><!-- End .entry-meta -->				<h3 class="entry-title text-center">					<a href="<?php echo get_the_permalink(); ?>"><?php the_title(); ?></a>				</h3><!-- End .entry-title -->				<div class="entry-content">					<p class="text-center"><?php echo  PostExcerpt::tns_post_excerpt(); ?></p>					<a href="<?php echo get_the_permalink(); ?>" class="read-more">مطالعه بیشتر</a>				</div><!-- End .entry-content -->			</div><!-- End .entry-body -->		</article><!-- End .entry -->	<?php endwhile; ?><?php else: ?>	<div class="alert alert-warning">پستی وجو ندارد!</div><?php endif; ?>