<?php$args = [	'limit'   => 15,	'status'  => 'publish',	'meta_key'=>'_tns_check_popular',	'meta_value'=> 1 ,	'orderby' => 'date',	'order'   => 'DESC'];$products_query = wc_get_products($args);if (!empty($products_query)) :	foreach ($products_query as $item) : ?>        <div class="col-6 col-md-4 col-lg-3 col-xl-5col">            <div class="product product-3 text-center">                <figure class="product-media">	                <?php if (!$item->is_in_stock()) : ?>                        <span class="product-label label-primary">ناموجود</span>	                <?php endif; ?>	                <?php if ( Utility::tns_calculateDiscountPercentage($item->get_regular_price(),$item->get_sale_price()) > 0 ) : ?>                        <span class="product-label label-circle label-sale"> تخفیف :  <?php echo Utility::tns_calculateDiscountPercentage($item->get_regular_price(),$item->get_sale_price()); ?> % </span>	                <?php elseif ( $item->is_on_sale()): ?>                        <span class="product-label label-sale">فروش ویژه</span>	                <?php endif; ?>                    <a href="<?php echo get_permalink($item->get_id()); ?>">                        <img src="<?php echo get_the_post_thumbnail_url($item->get_id()); ?>"                             alt="<?php echo esc_attr($item->get_name()); ?>" class="product-image">                    </a>                    <div class="product-action-vertical"></div>                </figure>                <div class="product-body">                    <div class="product-cat text-center">						<?php						$terms = get_the_terms($item->get_id(), 'product_cat');						if (!empty($terms) && !is_wp_error($terms)) {							$term_links = array_map(function ($term) {								return '<a href="' . esc_url(get_term_link($term)) . '">' . esc_html($term->name) . '</a>';							}, $terms);							echo implode(', ', $term_links);						}						?>                    </div>                    <h3 class="product-title text-center">                        <a href="<?php echo get_permalink($item->get_id()); ?>"><?php echo $item->get_name(); ?></a>                    </h3>                    <div class="product-price">						<?php if ($item->is_type('variable')) :							$min_price = $item->get_variation_price('min');							$max_price = $item->get_variation_price('max'); ?>                            <span class="new-price"><?php echo wc_price($min_price); ?></span>							<?php if ($min_price != $max_price) : ?>                            <span class="new-price"><?php echo wc_price($max_price); ?></span>						<?php endif; ?>						<?php elseif ($item->is_on_sale()) : ?>                            <span class="new-price"><?php echo wc_price($item->get_sale_price()); ?></span>                            <span class="old-price"><?php echo wc_price($item->get_regular_price()); ?></span>						<?php else : ?>                            <span class="new-price"><?php echo wc_price($item->get_price()); ?></span>						<?php endif; ?>                    </div>                </div>                <div class="product-footer">                    <div class="ratings-container">						<?php						$average = $item->get_average_rating();						$rating_count = $item->get_rating_count();						?>                        <div class="ratings">                            <div class="ratings-val" style="width: <?php echo ($average / 5) * 100; ?>%;"></div>                        </div>                        <span class="ratings-text">( <?php echo $rating_count; ?> دیدگاه )</span>                    </div>					<?php if ($item->is_type('variable')) :						$available_variations = $item->get_available_variations(); ?>                        <div class="product-nav product-nav-dots">							<?php foreach ($available_variations as $i => $available_variation): ?>                            <?php if (!empty($available_variation['attributes']['attribute_pa_color'])): ?>                                <a href="#" class="<?php echo $i === 0 ? 'active' : ''; ?>"                                   style="background: <?php echo esc_attr($available_variation['attributes']['attribute_pa_color']); ?>">                                    <span class="sr-only">نام رنگ</span>                                </a>                            <?php endif; ?>							<?php endforeach; ?>                        </div>					<?php endif; ?>                    <div class="product-action">                        <a href="<?php echo esc_url($item->add_to_cart_url()); ?>" class="btn-product btn-cart" title="افزودن به سبد خرید">                            <span>افزودن به سبد خرید</span>                        </a>                        <a href="<?php echo get_permalink($item->get_id()); ?>" class="btn-product btn-view-product" title="مشاهده سریع">                            <span>مشاهده محصول</span>                            <i class="icon-eye"></i>                        </a>                    </div>                </div>            </div>        </div>	<?php endforeach;endif;?>