<?php$search_query = get_search_query();$paged = (get_query_var('paged')) ? get_query_var('paged') : 1;$args = [	's' => $search_query,	'post_type' => 'post',	'posts_per_page' => 9,	'paged' => $paged,	'post_status' => 'publish'];$the_query = new WP_Query($args);if ($the_query->have_posts()) :	while ($the_query->have_posts()) : $the_query->the_post() ?><div class="entry-item lifestyle shopping col-sm-6 col-lg-4">    <article class="entry entry-mask">        <figure class="entry-media">            <a href="<?php the_permalink(); ?>">                <?php echo tns_post_thumbnail(); ?>            </a>        </figure><!-- End .entry-media -->        <div class="entry-body">            <div class="entry-meta">                <a href="#"><?php echo  get_the_date('j F Y'); ?></a>                <span class="meta-separator">|</span>                <a href="#"><?php  comments_number(); ?></a>            </div><!-- End .entry-meta -->            <h2 class="entry-title">                <a href="<?php the_permalink(); ?>"><?php echo get_the_title(); ?></a>            </h2><!-- End .entry-title -->            <div class="entry-cats">	            <?php the_category('، '); ?>            </div><!-- End .entry-cats -->        </div><!-- End .entry-body -->    </article><!-- End .entry --></div><!-- End .entry-item -->	<?php endwhile; ?><?php else: ?>    <div class="alert alert-warning">هیچ پستی منتشر نشده است!</div><?php endif; ?></div><!-- End .entry-container --></div></div><div class="mb-3"></div><!-- End .mb-3 --><nav aria-label="Page navigation" class="pagintestyle">	<?php echo Pagination::paginate($the_query); ?></nav>