<div class="widget">	<h3 class="widget-title">محبوب ترین اخبار</h3><!-- End .widget-title -->	<ul class="posts-list">        <?php        $args = [	        'post_type' => 'post',	        'posts_per_page' => 4,	        'orderby' => 'comment_count',	        'order' => 'DESC'        ];        $the_query = new WP_Query($args);        if ($the_query->have_posts()):        while ($the_query->have_posts()) : $the_query->the_post();        ?>		<li>			<figure>				<a href="<?php the_permalink() ?>">                    <?php echo tns_post_thumbnail(); ?>				</a>			</figure>			<div>				<span><?php echo human_time_diff(get_the_time('U'), current_time('timestamp')) . ' پیش'; ?></span>				<h4><a href="<?php the_permalink(); ?>>"><?php the_title() ?></a></h4>			</div>		</li>        <?php endwhile; ?>        <?php endif; ?>	</ul><!-- End .posts-list --></div><!-- End .widget -->