<div class="widget">	<h3 class="widget-title">برچسب ها</h3><!-- End .widget-title -->    <div class="tagcloud"><?php $tags =  get_terms(array(	'taxonomy' => 'post_tag',	'hide_empty' => false,));if ($tags) :foreach ($tags as $tag): $tag_link = get_tag_link($tag->term_id);    ?>		<a href="<?php echo $tag_link ?>">#<?php echo $tag->name ?></a>    <?php endforeach; ?>    <?php else: ?>     <div class="alert alert-info">تگ ها خبری خود را قرار دهید!</div>    <?php endif; ?>    </div><!-- End .tagcloud --></div><!-- End .widget -->