d_work_h']) ? $contact_us_data['end_work_h'] : '12';                     ?>                    <li>                        <i class="icon-clock-o"></i>                        <span class="text-dark">شنبه تا چهارشنبه</span><br><?php echo esc_html($start_work); ?> صبح تا <?php echo esc_html($end_work); ?> عصر                    </li>                    <li>                        <i class="icon-calendar"></i>                        <span class="text-dark">پنج‌شنبه</span><br><?php echo esc_html($start_work_h); ?> صبح تا <?php echo esc_html($end_work_h); ?> عصر                    </li>					<?php					$store_phone = !empty($contact_us_data['phone_number_store']) ? $contact_us_data['phone_number_store'] : '02112345678';					?>                    <li>                        <i class="icon-phone"></i>                        <a href="tel:<?php echo esc_attr($store_phone); ?>"><?php echo esc_html($store_phone); ?></a>                    </li>                </ul>            </div>        </div>    </div></div><!-- End .col-lg-6 -->