<div class="banner-group">	<div class="container">		<div class="row">            <?php                 global $wpdb;                 $table = $wpdb->prefix . 'tns_banner';            $banners = $wpdb->get_results("SELECT * FROM $table ORDER BY id DESC LIMIT 3", ARRAY_A);            if (!empty($banners)) :            foreach ($banners as $banner) :               ?>            <div class="col-md-6 col-lg-4">                <div class="banner">                    <a href="#">                        <img src="<?php echo esc_url($banner['image_url'])?>?>" alt="بنر">                    </a>                    <?php $main_title = explode('|',esc_html($banner['title'])) ;   ?>                    <div class="banner-content text-right">                        <h4 class="banner-title"><?php echo $main_title[0] ?></h4>                        <h3 class="banner-text"><?php echo $main_title[1] ?></h3>                        <!-- End .banner-title -->                        <a href="<?php echo esc_url($banner['link_url']); ?>" class="btn btn-outline-white banner-link btn-round bt-t-c">مشاهده</a>                    </div><!-- End .banner-content -->                </div><!-- End .banner -->            </div><!-- End .col-md-6 -->            <?php endforeach; ?>            <?php endif; ?>		</div><!-- End .row -->	</div><!-- End .container --></div><!-- End .banner-group -->