<div class="container">    <div class="intro-slider-container slider-container-ratio mb-2">        <div class="intro-slider owl-carousel owl-simple owl-light owl-nav-inside"             data-toggle="owl"             data-owl-options='{             "nav": false,             "rtl": true,             "items": 1,             "loop": false         }'>	        <?php	        global $wpdb;	        $table = $wpdb->prefix . 'tns_sliders';	        $sliders = $wpdb->get_results("SELECT * FROM $table ORDER BY id DESC LIMIT 3", ARRAY_A);	        if ($sliders) :		        foreach ( $sliders as $slider) : ?>                    <div class="intro-slide">                        <figure class="slide-image">                            <picture>                                <source media="(max-width: 480px)"                                        srcset="<?php echo esc_url($slider['p_image']) ?>">                                <img src="<?php echo esc_url($slider['p_image']) ?>" alt="Image Desc">                            </picture>                        </figure><!-- End .slide-image -->                        <div class="intro-content">                            <h3 class="intro-subtitle"><?php echo esc_html($slider['top_title']); ?></h3><!-- End .h3 intro-subtitle -->                            <h1 class="intro-title text-white"><?php echo esc_html($slider['main_title']); ?></h1>                            <!-- End .intro-title -->                            <div class="intro-price text-white"><?php echo esc_html($slider['sub_title']); ?></div><!-- End .intro-price -->                            <a href="<?php echo esc_url($slider['p_thumbnail']) ?>" class="btn btn-white-primary btn-round" id="btn-sliders">                                <span>خرید</span>                                <i class="icon-long-arrow-left"></i>                            </a>                        </div><!-- End .intro-content -->                    </div><!-- End .intro-slide -->		        <?php endforeach; ?>            <?php else: ?>            <div class="alert alert-info">هیج پستی در اسلایدر وجود ندارد!</div>	        <?php endif; ?>		</div><!-- End .intro-slider owl-carousel owl-simple -->		<span class="slider-loader"></span><!-- End .slider-loader -->	</div><!-- End .intro-slider-container --></div><!-- End .container -->