<?php if (have_posts()) :while (have_posts()) : the_post() ?><div class="entry-body">    <div class="entry-meta">                                        <span class="entry-author">                                            نویسنده : <a><?php echo get_the_author(); ?></a>                                        </span>        <span class="meta-separator">|</span>        <a><?php echo get_the_date('j F Y'); ?></a>        <span class="meta-separator">|</span>        <a><?php comments_number(); ?></a>	    <span class="meta-separator">|</span>	    <a>بازدید : <?php echo PostView::tns_get_post_view(get_the_ID()); ?></a>    </div><!-- End .entry-meta -->    <h2 class="entry-title">       <?php echo get_the_title() ?>    </h2><!-- End .entry-title -->    <div class="entry-cats">	    <?php	    $category = get_the_category();	    if ( ! empty( $category ) ) {		    $cat_link = get_category_link( $category[0]->term_id );		    echo '<a href="'.$cat_link.'">'.$category[0]->name.'</a>';	    }?>    </div><!-- End .entry-cats -->    <div class="entry-content editor-content">       <?php the_content(); ?>    </div><!-- End .entry-content -->    <div class="entry-footer row no-gutters flex-column flex-md-row">        <div class="col-md">            <div class="entry-tags">                <span>برچسب : </span>            <?php                $post_tags = get_the_tags();                if ($post_tags) {	                foreach ($post_tags as $tag) {		                $tag_name = $tag->name;		                $tag_link = get_tag_link($tag->term_id);		                echo '<a href="' . esc_url($tag_link) . '">#' . esc_html($tag_name) . '</a>';	                }                } else {	                echo '<div class="alert alert-info">تگی وجود ندارد!</div>';                }            ?>            </div><!-- End .entry-tags -->            <?php endwhile; ?>            <?php endif; ?>        </div><!-- End .col -->        <div class="col-md-auto mt-2 mt-md-0">            <div class="social-icons social-icons-color">            </div><!-- End .soial-icons -->        </div><!-- End .col-auto -->    </div><!-- End .entry-footer row no-gutters --></div><!-- End .entry-body -->