<?phpclass Utility {	/**	 * Calculate discount percentage between regular and sale price	 *	 * @param float|int|string $regular_price The regular price	 * @param float|int|string $sale_price The sale price	 * @return float The discount percentage (rounded up)	 */	public static function tns_calculateDiscountPercentage($regular_price, $sale_price): float	{		$regular = (float)$regular_price;		$sale = (float)$sale_price;		if ($regular <= 0 || $sale <= 0) {			return 0.0;		}		if ($regular > $sale) {			$discount = (($regular - $sale) / $regular) * 100;			return ceil($discount);		}		return 0.0;	}}